//
//  389. Find the Difference
//  https://leetcode.com/problems/find-the-difference/
//  Easy
//
//  Given two strings s and t which consist of only lowercase letters.
//  String t is generated by random shuffling string s and then add one more letter at a random position.
//  Find the letter that was added in t.
//
//  Example:
//  Input:
//  s = "abcd"
//  t = "abcde"

//  Output: 'e'
//  Explanation:
//  'e' is the letter that was added.
//

import XCTest

class L389_Find_the_Difference: XCTestCase {

    func a_findTheDifference(_ s: String, _ t: String) -> Character {
        let s = Array(s.sorted())
        let t = Array(t.sorted())
        for i in 0..<t.count {
            if i < s.count, t[i] != s[i] {
                return t[i]
            }
        }
        return t.last!
    }

    func b_findTheDifference(_ s: String, _ t: String) -> Character {
        var charDict: [Character: Int] = [:]
        let s = Array(s)
        for char in s {
            charDict[char, default: 0] += 1
        }
        
        let t = Array(t)
        for char in t {
            if let count = charDict[char] {
                if count == 0 {
                    return char
                }
                charDict[char] = count - 1
            } else {
                return char
            }
        }
        return Character(" ")
    }
    
    func testA1() {
        let s = "abcd"
        let t = "abcde"
        let output = a_findTheDifference(s, t)
        XCTAssertTrue(output == "e")
    }
    
    func testA2() {
        let s = ""
        let t = ""
        let output = a_findTheDifference(s, t)
        XCTAssertTrue(output == "t")
    }

    func testB1() {
        let s = "abcd"
        let t = "abcde"
        let output = b_findTheDifference(s, t)
        XCTAssertTrue(output == "e")
    }

    func testB2() {
        let s = ""
        let t = ""
        let output = b_findTheDifference(s, t)
        XCTAssertTrue(output == "t")
    }

}
